import React, { Component } from 'react';
import './Login.css'

class Login extends Component {
    constructor(props) {
        super(props)

        this.state = {
            isLoginFormShowing: false
        }
    }

    toggleLogin = () => {
        if (this.isLoginFormShowing) {
            this.setState({isLoginFormShowing: false});
        } else {
            this.setState({isLoginFormShowing: true});
        }
    }

    render() {
        // console.clear();

        // const loginBtn = document.getElementById('login');
        // const signupBtn = document.getElementById('signup');

        // loginBtn.addEventListener('click', (e) => {
        //     let parent = e.target.parentNode.parentNode;
        //     Array.from(e.target.parentNode.parentNode.classList).find((element) => {
        //         if (element !== "slide-up") {
        //             parent.classList.add('slide-up')
        //         } else {
        //             signupBtn.parentNode.classList.add('slide-up')
        //             parent.classList.remove('slide-up')
        //         }
        //     });
        // });

        // signupBtn.addEventListener('click', (e) => {
        //     let parent = e.target.parentNode;
        //     Array.from(e.target.parentNode.classList).find((element) => {
        //         if (element !== "slide-up") {
        //             parent.classList.add('slide-up')
        //         } else {
        //             loginBtn.parentNode.parentNode.classList.add('slide-up')
        //             parent.classList.remove('slide-up')
        //         }
        //     });
        // });

        console.log(this.state);
        var logInSlideUp;
        var signUpSlideUp;
        // if (this.isLoginFormShowing === false) {
        //     logInSlideUp = "slide-up";
        // } else {
        //     logInSlideUp = "";
        // }

        logInSlideUp = "slide-up";



        return (
            <div className="Login container">
                <div className="form-structor">
                    <div className={`signup ${signUpSlideUp}`}>
                        <h2 className="form-title" id="signup"><span>or</span>Sign up</h2>
                        <div className="form-holder">
                            <input type="text" className="input" placeholder="Name" />
                            <input type="email" className="input" placeholder="Email" />
                            <input type="password" className="input" placeholder="Password" />
                        </div>
                        <button className="submit-btn">Sign up</button>
                    </div>
                    <div className={`login ${logInSlideUp}`} >
                        <div className="center">
                            <h2 className="form-title" id="login" onClick={this.toggleLogin}><span>or</span>Log in</h2>
                            <div className="form-holder">
                                <input type="email" className="input" placeholder="Email" />
                                <input type="password" className="input" placeholder="Password" />
                            </div>
                            <button className="submit-btn">Log in</button>
                        </div>
                    </div>
                </div>


            </div>
        );
    }
}

export default Login;
