import React, {Component} from 'react';
import {CardElement, injectStripe} from 'react-stripe-elements';
import './CheckoutForm.css'

class CheckoutForm extends Component {
  constructor(props) {
    super(props);

    this.state = {
        complete: false
    }

    this.submit = this.submit.bind(this);
  }

  async submit(ev) {
    let {token} = await this.props.stripe.createToken({name: "Name"});
    let response = await fetch("/api/users/charge", {
      method: "POST",
      headers: {"Content-Type": "text/plain"},
      body: token.id,
    });

    if (response.ok) console.log("Purchase Complete!")
    if (response.ok) this.setState({ complete: true });
  }

  render() {
    if (this.state.complete) {
      return (
      <div id="Success-box-Container">
        <div id="success-box">
          <div className="dot" />
          <div className="dot two" />
          <div className="face">
            <div className="eye" />
            <div className="eye right" />
            <div className="mouth happy" />
          </div>
          <div className="shadow scale" />
          <div className="message"><h1 className="alert">Success!</h1><p>yay, everything is working.</p></div>
          <button className="button-box"><h1 className="green">continue</h1></button>
        </div>
        {/* <div id="error-box">
          <div className="dot" />
          <div className="dot two" />
          <div className="face2">
            <div className="eye" />
            <div className="eye right" />
            <div className="mouth sad" />
          </div>
          <div className="shadow move" />
          <div className="message"><h1 className="alert">Error!</h1><p>oh no, something went wrong.</p></div>
          <button className="button-box"><h1 className="red">try again</h1></button>
        </div> */}
      </div>);
    } 

    return (        
      <div className="checkout">
        <CardElement />
        <br/>
        <br/>

        <h1 className='Checkout-title roboto letter-spacing'>TOTAL BILL: ${this.props.displayTotalBill()}</h1>

        <button id="payBill" className="button is-warning" onClick={this.submit}>Finalize Payment</button>
      </div>
    );
  }
}

export default injectStripe(CheckoutForm);
